import { useState } from 'react'
import Greeting from './components/Greeting'
import jerry from './images/jerry.png'
import tommy from './images/tommy.png'
import SideImage from './components/SideImage'


import "./index.css";

const professions = [' an engineer', ' a trader', ' a researcher', ' a procrastinator']
const hobbies = ['reading books', 'playing chess', 'playing badminton', 'watching movies']

export default function App() {
  const [count, setCount] = useState(0)
  const [morning, setMorning] = useState(false)

  const dataImage = morning == 'Morning' ? tommy : jerry

  const increment = () => {
    if (count > 2) {
      setCount(0)
    } else {
      setCount(count + 1)
    }
  }

  setTimeout(increment, 1000)

  return (
    <div className='main-container'>
      <div className='container'>
        <Greeting setData={setMorning} />
        <h2>I am Junaid</h2>
        <h2>I live in Bengaluru</h2>
        <h2>I am <span className='bg-mask'>{professions[count]}</span></h2>
        <h2>I love <span className='bg-mask'>{hobbies[count]}</span></h2>
        <h2 className='lets-talk'>Let's talk</h2>
        <div className='links'>
          <a className='link' href='https://twitter.com/_bablujunaid' target='_blank' rel="noreferrer"> <i class="fa-brands fa-twitter-square fa-2x logo"></i> </a>
          <a className='link' href='https://www.linkedin.com/in/mdjunaid14/' target='_blank' rel="noreferrer"> <i class="fa-brands fa-linkedin fa-2x logo"></i> </a>
          <a className='link' href='https://github.com/mdjunaid14' target='_blank' rel="noreferrer"> <i class="fa-brands fa-github-square fa-2x logo"></i> </a>
          <a className='link' href='https://medium.com/@bablujunaid' target='_blank' rel="noreferrer"> <i class="fa-brands fa-medium fa-2x logo"></i> </a>
        </div>
      </div>
      <SideImage data={dataImage} />
    </div>
  );
}
