import { useState } from 'react'
import Greeting from './components/Greeting'


import "./index.css";

const professions = [' an engineer', ' a trader', ' a researcher', ' a procrastinator']
const hobbies = ['reading books', 'playing chess', 'playing badminton', 'watching movies']

export default function App() {
  const [count, setCount] = useState(0)

  const increment = () => {
    if (count > 2) {
      setCount(0)
    } else {
      setCount(count + 1)
    }
  }

  setTimeout(increment, 1000)

  return (
    <div className='main-container'>
      <div className='container'>
        <Greeting />
        <h2>I am Junaid</h2>
        <h2>I live in Bengaluru</h2>
        <h2>I am <span className='bg-mask'>{professions[count]}</span></h2>
        <h2>I love <span className='bg-mask'>{hobbies[count]}</span></h2>
        <h2 className='lets-talk'>Let's talk</h2>
        <div className='links'>
          <img className='logo' src='./logos/twitter.png' alt='twitter' />
          <img className='logo' src='' alt='linkedin' />
          <img className='logo' src='' alt='medium' />
          <img className='logo' src='' alt='github' />
        </div>
      </div>
      <div className='image'>
        <img className='inner-image' src='' alt='inner-image' />
      </div>
    </div>
  );
}
