import { useState } from 'react'
import Greeting from './components/Greeting'
// import twitter from './logos/twitter-logo.png'
// import linkedin from './logos/linkedin-logo.png'
// import medium from './logos/medium-logo.png'
// import github from './logos/github-logo.png'
import jerry from './images/jerry.png'
import tommy from './images/tommy.png'


          // <img className='logo' src={twitter} alt='twitter' />
          // <img className='logo' src={linkedin} alt='linkedin' />
          // <img className='logo' src={medium} alt='medium' />
          // <img className='logo' src={github} alt='github' />


import "./index.css";

const professions = [' an engineer', ' a trader', ' a researcher', ' a procrastinator']
const hobbies = ['reading books', 'playing chess', 'playing badminton', 'watching movies']

export default function App() {
  const [count, setCount] = useState(0)

  const increment = () => {
    if (count > 2) {
      setCount(0)
    } else {
      setCount(count + 1)
    }
  }

  setTimeout(increment, 1000)

  return (
    <div className='main-container'>
      <div className='container'>
        <Greeting />
        <h2>I am Junaid</h2>
        <h2>I live in Bengaluru</h2>
        <h2>I am <span className='bg-mask'>{professions[count]}</span></h2>
        <h2>I love <span className='bg-mask'>{hobbies[count]}</span></h2>
        <h2 className='lets-talk'>Let's talk</h2>
        <div className='links'>
          <i class="fa-brands fa-twitter-square fa-2x logo"></i>
          <i class="fa-brands fa-linkedin fa-2x logo"></i>
          <i class="fa-brands fa-medium fa-2x logo"></i>
          <i class="fa-brands fa-github-square fa-2x logo"></i>
          <i class="fa-brands fa-github fa-2x logo"></i>
        </div>
      </div>
      <div className='side-image'>
        <img className='inner-image' src={jerry} alt='tom-jerry' />
      </div>
    </div>
  );
}
